import React, {Component} from 'react';

class Welcome extends Component {
  render() {
    return (
      <div>
        <Greet name="leo" />
        <Greet name="poppy" />
      </div>
    );
  }
}

class Greet extends Component {
  render() {
    return (
      <h1> Hello, {this.props.name} </h1>
    )
  }
}

export default Welcome;
