import React from 'react'

function NumberLists(props) {
  const numbers = props.numbers;
  const listItems = numbers.map((number) =>
    // 1:
    // Warning: Each child in an array or iterator should have a unique "key" prop.
    // Check the render method of `NumberLists`.
    // <li>{number}</li>

    // 2:
    <li key={number.toString()}>
      {number}
    </li>

    // 3: Keys help React identify which items have changed, are added, or are removed.
    // Keys should be given to the elements inside the array to give the elements a stable identity:
    // <li key={todo.id}>
    //   {todo.text}
    // </li>
    
    // 4: 
    // When you don't have stable IDs for rendered items, you may use the item index as a key as a
    // last resort:
    // const todoItems = todos.map((todo, index) =>
    //   // Only do this if items have no stable IDs
    //   <li key={index}>
    //     {todo.text}
    //   </li>
    // );
    // We don't recommend using indexes for keys if the items can reorder, as that would be slow. 
  );
  return (
    <ul>{listItems}</ul>
  );
}

const numbers = [1,2,3,4,5];

function ListsNKeys() {
  return (
    <NumberLists numbers={numbers} />
  );
}
export default ListsNKeys;

